const webm =
  'data:video/webm;base64,';

const mp4 =
  'data:video/mp4;base64, ';

// Detect iOS browsers < version 10
const oldIOS = () =>
  typeof navigator !== 'undefined' &&
  parseFloat(
    (
      '' +
      (/CPU.*OS ([0-9_]{3,4})[0-9_]{0,1}|(CPU like).*AppleWebKit.*Mobile/i.exec(
        navigator.userAgent
      ) || [0, ''])[1]
    )
      .replace('undefined', '3_2')
      .replace('_', '.')
      .replace('_', '')
  ) < 10 &&
  !window.MSStream;

// Detect native Wake Lock API support
const nativeWakeLock = () => 'wakeLock' in navigator;

class NoSleep {
  constructor () {
    this.enabled = false;
    if (nativeWakeLock()) {
      this._wakeLock = null;
      this.handleVisibilityChange = () => {
        if (this._wakeLock !== null && document.visibilityState === 'visible') {
          this.enable();
        }
      };
      document.addEventListener(
        'visibilitychange',
        this.handleVisibilityChange
      );
      document.addEventListener(
        'fullscreenchange',
        this.handleVisibilityChange
      );
    } else if (oldIOS()) {
      this.noSleepTimer = null;
    } else {
      // Set up no sleep video element
      this.noSleepVideo = document.createElement('video');

      this.noSleepVideo.setAttribute('playsinline', '');

      this._addSourceToVideo(this.noSleepVideo, 'mp4', mp4);
      this._addSourceToVideo(this.noSleepVideo, 'webm', webm);

      this.noSleepVideo.addEventListener('loadedmetadata', () => {
        if (!this.noSleepVideo) return;
        if (this.noSleepVideo.duration <= 1) {
          // webm source
          this.noSleepVideo.setAttribute('loop', '');
        } else {
          this.onTimeupdate = () => {
            if (this.noSleepVideo.currentTime > 0.5) {
              this.noSleepVideo.currentTime = Math.random();
            }
          };
          // mp4 source
          this.noSleepVideo.addEventListener('timeupdate', this.onTimeupdate);
        }
      });
    }
  }

  _addSourceToVideo (element, type, dataURI) {
    var source = document.createElement('source');
    source.src = dataURI;
    source.type = `video/${type}`;
    element.appendChild(source);
  }

  get isEnabled () {
    return this.enabled;
  }

  enable () {
    if (nativeWakeLock()) {
      return navigator.wakeLock
        .request('screen')
        .then((wakeLock) => {
          this._wakeLock = wakeLock;
          this.enabled = true;
          console.log('Wake Lock active.');
          this._wakeLock.addEventListener('release', () => {
            // ToDo: Potentially emit an event for the page to observe since
            // Wake Lock releases happen when page visibility changes.
            // (https://web.dev/wakelock/#wake-lock-lifecycle)
            console.log('Wake Lock released.');
          });
        })
        .catch((err) => {
          this.enabled = false;
          console.error(`${err.name}, ${err.message}`);
        });
    } else if (oldIOS()) {
      this.disable();
      console.warn(`
        NoSleep enabled for older iOS devices. This can interrupt
        active or long-running network requests from completing successfully.
        See https://github.com/richtr/NoSleep.js/issues/15 for more details.
      `);
      this.noSleepTimer = window.setInterval(() => {
        if (!document.hidden) {
          window.location.href = window.location.href.split('#')[0];
          window.setTimeout(window.stop, 0);
        }
      }, 15000);
      this.enabled = true;
      return Promise.resolve();
    } else {
      if (!this.noSleepVideo.paused) return;
      let playPromise = this.noSleepVideo.play();
      return playPromise
        .then((res) => {
          this.enabled = true;
          return res;
        })
        .catch((err) => {
          console.log('noSleep: ', err.message);
          this.enabled = false;
        });
    }
  }

  disable () {
    if (nativeWakeLock()) {
      if (this._wakeLock) {
        this._wakeLock.release();
      }
      this._wakeLock = null;
    } else if (oldIOS()) {
      if (this.noSleepTimer) {
        console.warn(`
          NoSleep now disabled for older iOS devices.
        `);
        window.clearInterval(this.noSleepTimer);
        this.noSleepTimer = null;
      }
    } else {
      this.noSleepVideo.pause();
    }
    this.enabled = false;
  }

  toPlay () {
    if (!this.noSleepVideo) return;
    if (!this.noSleepVideo.paused) return;

    let now = performance.now();
    if (!this.lastTs) {
      this.lastTs = now;
    }
    if (now - this.lastTs > 10000) {
      this.enable();
      this.lastTs = now;
    }
  }

  destroy () {
    this.disable();
    if (this.handleVisibilityChange) {
      document.removeEventListener(
        'visibilitychange',
        this.handleVisibilityChange
      );
      document.removeEventListener(
        'fullscreenchange',
        this.handleVisibilityChange
      );
    }
    if (this.onTimeupdate) {
      this.noSleepVideo.removeEventListener('timeupdate', this.onTimeupdate);
    }
    this.noSleepVideo = null;
  }
}

export default NoSleep;
